import create from 'zustand'

export const useButtonSide = create((set) => ({
  status: false,
  setStatus: () => set((state) => ({ status: state.status = true })),
  removeStatus: () => set(({ status: false }))
}));

export const getUrlNow = create((set) => ({
  url: false,
  setUrl: () => set(() => ({ url: window.location.href.split('/')[3] }))
}))

export default { useButtonSide, getUrlNow }
