import './Sidebar.css';
import { BsHouse, BsExclamationCircle, BsImage, BsHeart } from 'react-icons/bs';
import { IconContext } from 'react-icons';
import useButtonSide from '../../store/index';
import { useEffect, useRef } from 'react';

const Sidebar = ({ NavLink }) => {

  const getButton = useButtonSide((state) => state.status);
  const removeButton = useButtonSide((state) => state.removeStatus);
  const asideRef = useRef();

  useEffect(() => { getButton ? asideRef.current.classList.add('active') : asideRef.current.classList.remove('active'); })

  const active = 'active';
  return (
    <>
      <aside onClick={removeButton} className='container aside' ref={asideRef}>
        <div className='profile-aside'>
          <figure>
            <img src={require('../../images/profile/kurumi-avatar.png')} alt="" />
            <figcaption>
              <ul>
                <IconContext.Provider value={{ className: 'icons-menu' }} >
                  <NavLink to='/' className={({ isActive }) => isActive ? active : undefined} ><li><BsHouse /><p>home</p></li></NavLink>
                  <li><BsExclamationCircle /><p>about</p></li>
                  <li><BsImage /><p>album</p></li>
                  <li><BsHeart /><p>waifu</p></li>
                </IconContext.Provider>
              </ul>
            </figcaption>
          </figure>
        </div>
      </aside>
    </>
  )
}
export default Sidebar;
