import create from 'zustand'

const useButtonSide = create((set) => ({
  status: false,
  setStatus: () => set((state) => ({
    status: state.status = true
  })),
  removeStatus: () => set(({ status: false }))
}))

const useUrlPathname = create((set) => ({
  path: undefined,
  setPath: () => {
    const url = window.location.pathname.split('/')[1];
    set((state) => ({
      path: url
    }))
  }
}))

export {
  useButtonSide,
  useUrlPathname
}
