import './ButtonBack.css';
import { BsCaretUpFill } from 'react-icons/bs';
import { Link } from 'react-scroll';
import { useEffect, useState } from 'react';

const ButtonBack = () => {
  const [getUrl, setUrl] = useState('');

  useEffect(() => {
    setUrl(window.location.pathname.split('/')[3])
  })

  const url = () => {
    const x = window.location.pathname.split('/')[1];
    (x === '') ? setUrl('home') : setUrl(x);
    console.log(getUrl)
  }

  console.log(getUrl,'1')
  return (
    <div className='container button-back'>
      <div className='mdui-ripple mdui-ripple-black'>
        <Link onClick={() => url()} to={getUrl} smooth={true} offset={-100} duration={1000} ><BsCaretUpFill values={{ className: 'icon' }} /></Link>
      </div>
    </div>
  )
}

export default ButtonBack;
